  private void txtDangNhapKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtDangNhapKeyPressed
    if (evt.getKeyCode() == evt.VK_ENTER) {
      txtMatKhau.requestFocus();
    }
  }//GEN-LAST:event_txtDangNhapKeyPressed

  private void txtMatKhauKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtMatKhauKeyPressed
    if (evt.getKeyCode() == evt.VK_ENTER) {
      send();
    }
  }//GEN-LAST:event_txtMatKhauKeyPressed

  private void btnDangNhapKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_btnDangNhapKeyReleased
    if (evt.getKeyCode() == evt.VK_ENTER) {
      send();
    }
  }//GEN-LAST:event_btnDangNhapKeyReleased

  private void btnThoatKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_btnThoatKeyReleased
    if (evt.getKeyCode() == evt.VK_ENTER) {
      System.exit(0);
    }
  }//GEN-LAST:event_btnThoatKeyReleased

  public void resetForm() {
    txtDangNhap.setText("");
    txtMatKhau.setText("");
    txtDangNhap.requestFocus();
  }

  public void send() {
    String strNull = "";
    String username = txtDangNhap.getText();
    if (username.equals(strNull) || txtMatKhau.getText().equals(strNull)) {
      this.resetForm();
    }
    char[] arr = txtMatKhau.getPassword();
    String password = new String(arr);

    // Tạo thông tin user
    Systems user = new Systems();
    user.setUsername(username);
    user.setPassword(password);

    // Kiểm tra đăng nhập trong DB
    SystemsDAO bussiness = new SystemsDAO();
    boolean isLoginSuccess = bussiness.isHasUser(user);

    if (isLoginSuccess) {
      JOptionPane.showMessageDialog(rootPane, "Success");
      this.dispose();
      frmMain1.enableMenu();
    } else {
      JOptionPane.showMessageDialog(rootPane,
              "Fail");
    }
  }

